$(document).ready(function() {
    var $shutter = $('#cameraShutter');

    var photoImg = document.getElementsByClassName("info-photo");
    photoImg.style.opacity = '0.0';
    photoImg.style.filter = 'alpha(opacity=0)';

    var unsplash = new Image(320, 320);
    unsplash.src = 'https://source.unsplash.com/user/yanss/likes/320x320';
    unsplash.onload = function() {
        photoImg = unsplash;
    }

    function printPhoto() {
        $('.info-content').toggleClass('animate');
        $('.camera .glass').toggleClass('active');
    }

    $shutter.on('click', printPhoto);

    setTimeout(printPhoto, 500);

    console.log('© zchen9 🙋 2015-' + (new Date()).getFullYear());
    console.log('特别感谢zchen9小仙女的精彩主题，希望大家去follow她的github：https://github.com/zchen9');

});